#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\mortal\packages\mortal-cli\bin\node_modules;D:\mortal\packages\mortal-cli\node_modules;D:\mortal\packages\node_modules;D:\mortal\node_modules;D:\node_modules;D:\mortal\examples\app\node_modules;D:\mortal\examples\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/mortal/packages/mortal-cli/bin/node_modules:/mnt/d/mortal/packages/mortal-cli/node_modules:/mnt/d/mortal/packages/node_modules:/mnt/d/mortal/node_modules:/mnt/d/node_modules:/mnt/d/mortal/examples/app/node_modules:/mnt/d/mortal/examples/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../packages/mortal-cli/bin/index.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../packages/mortal-cli/bin/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../packages/mortal-cli/bin/index.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../packages/mortal-cli/bin/index.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
